#!/usr/bin/env python3# -*- coding: utf-8 -*-import sysimport csvtry:    file = sys.argv[1]    # Python 3ではバイナリモード('rb')ではなくテキストモードで開く    #もし sjis or cp932 の場合 encoding='cp932' とする    with open(file, 'r', encoding='utf-8', newline='') as f:        reader = csv.reader(f)        for row in reader:            s = row[45]            #print(s[-4:])            if s[-4:] == '.zip':                print(s)           except Exception as e:    # 例外を黙殺せず、デバッグ用に表示するほうが良い    print(f"Error: {e}", file=sys.stderr)